.material-switch > label.switch::before { background: rgb(0, 0, 0); border-radius: 8px; box-shadow: rgba(0, 0, 0, 0.5) 0px 0px 10px inset; content: ""; height: 1pc; margin-top: -10px; opacity: 0.3; position: absolute; transition: all 0.4s ease-in-out 0s; width: 40px; }
.material-switch > label.switch::after { background: rgb(255, 255, 255); border-radius: 1pc; box-shadow: rgba(0, 0, 0, 0.3) 0px 0px 5px; content: ""; height: 24px; left: -4px; margin-top: -10px; position: absolute; top: -4px; transition: all 0.3s ease-in-out 0s; width: 24px; }
.material-switch > input[type="checkbox"]:checked + label.switch::before { background: inherit; opacity: 0.5; }
.material-switch > input[type="checkbox"]:checked + label.switch::after { background: inherit; left: 20px; }
.material-switch label { cursor: pointer; font-weight: 400; }

.sw-dropdown { position: relative; }
.sw-dropdown-in { position: relative; padding: 8px 10px; margin: 8px 10px; background-color: rgb(255, 255, 255); z-index: 100; }
.sw-dropdown-content { display: none; position: absolute; border-radius: 10px; background: none 0% 0% repeat scroll rgb(255, 255, 255); box-shadow: rgb(192, 192, 192) 0px 0px 15px 0px; z-index: 5; }
.sw-dropup-content { bottom: 100%; }
.sw-dropright-content { right: 0px; }
.sw-show.sw-dropdown-content { display: block; }

/* sw-dropdown js */

function swDrop(
  triggerId,
  swDropdownContentId,
  swDropdownId,
  animateIn,
  animateOut,
  animateDuration
) {
  // $('#' + swDropdownContentId).addClass('sw-dropdown-content');
  $("#" + swDropdownContentId).css({
    "animation-duration": animateDuration + "s"
  });

  // Toggle on trigger click
  $("#" + triggerId).on("click", function(e) {
    if ($("#" + swDropdownContentId).hasClass("sw-show")) {
      //$('.sw-dropdown-content').removeClass('fadeInDown');
      if (animateIn !== "sw-none") {
        $("#" + swDropdownContentId).addClass(animateOut);
        $("#" + swDropdownContentId).one(
          "webkitAnimationEnd mozAnimationEnd MSAnimationEnd oanimationend animationend",
          function() {
            $("#" + swDropdownContentId).removeClass("sw-show");
            $("#" + swDropdownContentId)
              .hide()
              .trigger("hidden");
            //$('#' + swDropdownContentId).css('display', 'none');
            $("#" + swDropdownContentId).removeClass(animateOut);
          }
        );
      } else {
        $("#" + swDropdownContentId).removeClass("sw-show");
        $("#" + swDropdownContentId)
          .hide()
          .trigger("hidden");
        //$('#' + swDropdownContentId).css('display', 'none');
      }
    } else {
      //$('.sw-dropdown-content').removeClass('fadeOutUp);
      if (animateIn !== "sw-none") {
        $("#" + swDropdownContentId).addClass(animateIn);
        $("#" + swDropdownContentId).addClass("sw-show");
        $("#" + swDropdownContentId)
          .show()
          .trigger("shown");
        //$('#' + swDropdownContentId).css('display', 'block');
        $("#" + swDropdownContentId).one(
          "webkitAnimationEnd mozAnimationEnd MSAnimationEnd oanimationend animationend",
          function() {
            $("#" + swDropdownContentId).removeClass(animateIn);
          }
        );
      } else {
        $("#" + swDropdownContentId).addClass("sw-show");
        $("#" + swDropdownContentId)
          .show()
          .trigger("shown");
        //$('#' + swDropdownContentId).css('display', 'block');
      }
    }
  });

  // Close on click outside dropdown
  $("html").on("click", function(e) {
    //console.log($(e.target).parents().end().attr('class').indexOf("datepicker"));
    //console.log( $(e.target) );
    var parentClass = $(e.target).parents().end().attr('class');
    if (typeof parentClass == "undefined") {
      parentClass = $(e.target).parents().attr('class');
    }
    if (typeof parentClass == "undefined") {
      parentClass = "";
    }
    if (
      //$(e.target).is("html") |
      !$("#" + swDropdownId).is(e.target) &&
      $("#" + swDropdownId).has(e.target).length === 0 &&
      $("#" + swDropdownId).find(e.target).length === 0 &&
      !(
        $(e.target).parents('.shiny-input-container').length
      ) && // hack for bootstrap select
      !(
        $(e.target).parents('.bootstrap-select').length
      ) && // hack for pickr color
      !(
        $(e.target).parents('.pcr-app').length
      ) && // hack for shinytree
      !(
        $(e.target).prop("tagName") === "I" &&
        $(e.target).attr("class") === "jstree-icon jstree-ocl"
      ) && // hack for DT
      !(
        $(e.target).prop("tagName") === "A" &&
        RegExp("paginate_button").test($(e.target).attr("class")) === true
      ) && // hack for airdatepicker
      (
        parentClass.indexOf("datepicker") < 0
      ) && // hack for airdatepicker2
      (
        parentClass.indexOf("airdatepicker") < 0
      ) && // hack for selectize
      !(
        $(e.target).parents('.selectize-input').length
      ) && // hack for selectize
      !(
        $(e.target).hasClass("remove")
      ) && // hack for selectize
      !(
        $(e.target).prop("tagName") === "DIV" &&
        $(e.target).attr("class") === "selectize-dropdown-content"
      ) &&
      !(
        $(e.target).prop("tagName") === "DIV" &&
        RegExp("option").test($(e.target).attr("class")) === true
      )
    ) {
      if ($("#" + swDropdownContentId).hasClass("sw-show")) {
        if (animateIn !== "sw-none") {
          $("#" + swDropdownContentId).removeClass(animateIn);
          $("#" + swDropdownContentId).addClass(animateOut);
          $("#" + swDropdownContentId).one(
            "webkitAnimationEnd mozAnimationEnd MSAnimationEnd oanimationend animationend",
            function() {
              $("#" + swDropdownContentId).removeClass("sw-show");
              $("#" + swDropdownContentId)
                .hide()
                .trigger("hidden");
              //$('#' + swDropdownContentId).css('display', 'none');
              $("#" + swDropdownContentId).removeClass(animateOut);
            }
          );
        } else {
          $("#" + swDropdownContentId).removeClass("sw-show");
          $("#" + swDropdownContentId)
            .hide()
            .trigger("hidden");
          //$('#' + swDropdownContentId).css('display', 'none');
        }
      }
    }
  });
}

